        comment: comment,
      }),
    })
      .then((r) => r.json())
      .then((newComment) => {
        setSportCar([...sport, newComment]);
      });
    setComments("");
  }

  return (
    <div className="container">
      <br></br>
      <div className="content">
        <div>{name}</div>
      </div>
      <div >
        <img
          src={showFront ? image.front : image.back}
          onClick={toggle}
