      <div className="sub-text">
        <div>{hp}</div>
      </div>
      <div className="sub-text">
        <div>{engine}</div>
      </div>
      <div className="sub-text">
        <div>Top Speed: {topSpeed}</div>
      </div>
      <div className="sub-text">
        <div>${price}</div>
      </div>
      <div className="sub-text">
        <div>{comment}</div>
      </div>
      <button onClick={handleDeleteClick} className="del-btn">
        Impound
      </button>
      <button
        className={
          emoji ? "like" : "like"
        }
