//     ‚Ä¢ then (unclalmed -> (
//     setTickets (current -‚Ä∫ current.filter((ticket) -‚Ä∫ unclaimed.id I-- ticket. id))
//     })
// }

  return (
    <div className="container">
      <br></br>
      <div className="content">
        <div>{name}</div>
      </div>
      <div>
        <img
          src={showFront ? image.front : image.back}
          onClick={toggle}
          alt="mom"
          className="image"
        />
      </div>
      <div className="sub-text">
        <div>Hp:{hp}</div>
      </div>
      <div className="sub-text">
        <div>{engine}</div>
      </div>
      <div className="sub-text">
        <div>Top Speed: {topSpeed}</div>
      </div>
      <div className="sub-text">
        <div>${price}</div>
      </div>
      <button onClick={handleDeleteClick} className="del-btn">
        Impound
      </button>
      <div className="sub-text">
        <div>{comment}</div>
      </div>
      <button
        className={
          emoji ? "like" : "like"
        }
        onClick={emojiToggle}
      >
        {" "}
        {emoji ? "‚ù§Ô∏è" : "ü§Æ"}
      </button>
    </div>
  );
}
export default MomCarCard;
