import React, { useState } from "react";

function HyperCard({hyper}) {

    const {name, hp, engine, topSpeed, price, image} = hyper
    const [showFront, setShowFront] = useState("")

function toggle() {
    setShowFront((showFront) => !showFront)
}

    return (
        <div>
             <div className="content">
                <div>{name}</div>
            </div>
            <div className="image">
                <img src= {showFront ? image.front : image.back} onClick= {toggle} />
            </div>
            <div>
                <div> {hp} </div>
            </div>
            <div>
                <div> {engine} </div>
            </div>

        </div>
    )
}

export default HyperCard;
