import React, { useState } from "react";

function HyperCard( {hyper, onDeleteHyperCar} ) {

    const {id, name, hp, engine, topSpeed, price, image} = hyper
    const [showFront, setShowFront] = useState("")



function toggle() {
    setShowFront((showFront) => !showFront)
}

function handleDeleteClick() {
    fetch(`http://localhost:3000/hypercars/${id}`, {
        method: "DELETE",
    })
      .then((r) => r.json())
}

    return (
        <div>
            <br></br>
             <div className="content">
                <div>{name}</div>
            </div>
            <div className="image" >
                <img src= {showFront ? image.front : image.back} onClick= {toggle} alt="hyper"/>
            </div>
            <div className="hp">
                <div>{hp}</div>
            </div>
            <div className="engine">
                <div>{engine}</div>
            </div>
            <div className="speed">
                <div>mph:{topSpeed}</div>
            </div>
            <div className="price">
                <div>${price}</div>
            </div>
        </div>
    )
}

export default HyperCard;
