import React, { useState } from "react";

function HyperCard( {hyper, onDeleteHyperCar} ) {

    const {id, name, hp, engine, topSpeed, price, image, comment} = hyper
    const [showFront, setShowFront] = useState("");
    const [emoji, setEmoji]= useState(true);

function emojiToggle() {
    setEmoji((emoji) => !emoji)
}

function toggle() {
    setShowFront((showFront) => !showFront)
}

function handleDeleteClick() {
    fetch(`http://localhost:3000/hypercars/${id}`, {
        method: "DELETE",
    })
      .then((r) => r.json())
      .then(() => {
        onDeleteHyperCar(hyper);
      })
}

    return (
        <div>
            <br></br>
             <div className="content">
                <div>{name}</div>
            </div>
            <div className="image" >
                <img src= {showFront ? image.front : image.back} onClick= {toggle} alt="hyper"/>
            </div>
            <div className="hp">
                <div>{hp}</div>
            </div>
            <div className="engine">
                <div>{engine}</div>
            </div>
            <div className="speed">
                <div>Top Speed: {topSpeed}</div>
            </div>
            <div className="price">
                <div>${price}</div>
            </div>
            <button onClick= {handleDeleteClick} className="del-btn">
                Impound
            </button> 
            <button className= {emoji ? "emoji-button favorite active" : "emoji-button favorite"}  onClick= {emojiToggle}> {emoji ? "‚ù§Ô∏è" : "ü§Æ"}</button>
           {/* ‚ô• */}
           {/* ‚ù§Ô∏èÔ∏è */}
        </div>
    )
}

export default HyperCard;
