import React, { useEffect, useState } from "react";
import MomsCarForm from "./MomsCarForm";
import MomCarCard from "./MomCarCard";

function MomsCarPage() {

   const [momsCar, setMomsCar]= useState([]);

   function handleAddMomCar( newMomCar ) {
    const updateMomCarArray = [...momsCar, newMomCar];  
    setMomsCar(updateMomCarArray);
    console.log(updateMomCarArray)
   }

   useEffect (() => {
    fetch("http://localhost:3000/momcar")
      .then((r) => r.json())
      .then((momsData) => {
        setMomsCar(momsData)
      })
   }, [])

const displayMomCar = momsCar.map((momCar) => {
  return (
    <MomCarCard momCar= {momCar} key= {momCar.id} />
  )
})

    return (
        <div>
            <MomsCarForm onAddMomCar= {handleAddMomCar} />
            {momsCar.map((momCar) => {
              return (
                <MomCarCard momCar= {momCar} key= {momCar.id} />
              )
            })}
        </div>
    )
}

export default MomsCarPage
