import React, { useState } from "react";

function SportCarCard( {sport, onDeleteSportCar} ) {


    const {id, name, hp, engine, topSpeed, price, image} = sport
    const [showFront, setShowFront] = useState("")
    const [emoji, setEmoji]= useState(true);

    function emojiToggle() {
        setEmoji((emoji) => !emoji)
    }

    function toggle() {
    setShowFront((showFront) => !showFront)
    }

    function handleDeleteClick() {
        fetch(`http://localhost:3000/sportcars/${id}`, {
            method: "DELETE",
        })
          .then((r) => r.json())
          .then(() => {
            onDeleteSportCar(sport);
          })
    }

    return (
        <div>
            <br></br>
             <div className="content">
                <div>{name}</div>
            </div>
            <div className="image" >
                <img src= {showFront ? image.front : image.back} onClick= {toggle} alt="sport"/>
            </div>
            <div className="hp">
                <div>{hp}</div>
            </div>
            <div className="engine">
                <div>{engine}</div>
            </div>
            <div className="speed">
                <div>mph:{topSpeed}</div>
            </div>
            <div className="price">
                <div>${price}</div>
            </div>
            <button onClick= {handleDeleteClick} className="del-btn">
                Donate to Charity
            </button> 
            <button className= {emoji ? "emoji-button favorite active" : "emoji-button favorite"}  onClick= {emojiToggle}> {emoji ? "‚ù§Ô∏è" : "ü§Æ"}</button>
        </div>
    )
}

export default SportCarCard;
