import React, { useState } from "react";

function SportCarCard( {sport} ) {


    const {name, hp, engine, topSpeed, price, image} = sport
    const [showFront, setShowFront] = useState("")

function toggle() {
    setShowFront((showFront) => !showFront)
}
    return (
        <div>
            <br></br>
             <div className="content">
                <div>{name}</div>
            </div>
            <div className="image" >
                <img src= {showFront ? image.front : image.back} onClick= {toggle} alt="sport"/>
            </div>

        </div>
    )
}

export default SportCarCard;
