import React, { useState } from "react";

function MomCarCard( {momCar, onDeleteMomCar} ) {

    const {id, name, hp, engine, topSpeed, price, image} = momCar
    const [showFront, setShowFront] = useState("")
    const [emoji, setEmoji]= useState(true);

    function emojiToggle() {
        setEmoji((emoji) => !emoji)
    }

    function toggle() {
        setShowFront((showFront) => !showFront)
    }

    function handleDeleteClick() {
        fetch(`http://localhost:3000/momcar/${id}`, {
            method: "DELETE",
        })
          .then((r) => r.json())
          .then(() => {
            onDeleteMomCar(momCar);
          })
    }

    return (
        <div>
            <br></br>
             <div className="content">
                <div>{name}</div>
            </div>
            <div className="image" >
                <img src= {showFront ? image.front : image.back } onClick= {toggle} alt="mom"/>
            </div>
            <div className="hp">
                <div>Hp:{hp}</div>
            </div>
            <div className="engine">
                <div>{engine}</div>
            </div>
            <div className="speed">
                <div>Top Speed: {topSpeed}</div>
            </div>
            <div className="price">
                <div>${price}</div>
            </div>
            <button onClick= {handleDeleteClick} className="del-btn">
                Impound
            </button> 
            <button className= {emoji ? "emoji-button favorite active" : "emoji-button favorite"}  onClick= {emojiToggle}> {emoji ? "‚ù§Ô∏è" : "ü§Æ"}</button>
        </div>
    )
}
export default MomCarCard;
